templates.list:
{{#if value}}
    <span class="ellipsis_inline">
    {{#each value}}
        {{#notEq @index 0}}, {{/notEq}}<a title="{{id}}" href="{{href}}">{{id}}</a>{{#if reason_code}} ({{reason}}){{/if}}
    {{/each}}
    </span>
{{/if}}

templates.edit:
{{#each value}}
    <div>
        <div class="span5">
            <input type="hidden" name="related_opportunities" class="select2 inherit-width related_opportunity" value="{{id}}" data-id="{{@index}}" data-index="{{@index}}" style="width: 150px">
        </div>
        <div class="span7 control-group">
            <div class="controls controls-two btn-fit">
                <select name="reason_code" class="select2 inherit-width reason_code" data-id="{{../name}}{{@index}}" data-index="{{@index}}" style="width: 150px">
                    {{#each ../reasonCodes}}
                        <option value="{{@key}}" {{#eq @key ../reason_code}}selected {{/eq}}>{{this}}</option>
                    {{/each}}
                </select>
                {{#if remove_button}}
                    <button type="button" class="btn second" name="remove" data-index="{{@index}}" rel="tooltip" title="{{str "LBL_REMOVE"}}"><i class="fa fa-minus"></i></button>
                {{/if}}
                {{#if add_button}}
                    <button type="button" class="btn first" name="add" data-index="{{@index}}" rel="tooltip" title="{{str "LBL_ADD"}}"><i class="fa fa-plus"></i></button>
                {{/if}}
            </div>
        </div>
    </div>
{{/each}}

{{#if hideCurrencyDropdown}}
    <input type="text" name="{{name}}" value="{{value}}" class="inherit-width"{{#if def.tabindex}} tabindex="{{def.tabindex}}"{{/if}}>
{{else}}
    <div class="select2-addon">
        <span sfuuid="{{currencySfId}}" class="currency edit"></span>
        <input type="text" name="{{name}}" value="{{value}}"{{#if def.tabindex}} tabindex="{{def.tabindex}}"{{/if}}>
    </div>
{{/if}}

{{#eachOptions items}}
    <span class="radioenum-inline">
        <label>
            <input type="radio" name="{{../name}}" value="{{key}}"
                {{#if def.tabindex}} tabindex="{{def.tabindex}}"{{/if}}{{#eq key ../value}}checked{{/eq}}>
            {{value}}
        </label>
    </span>
{{/eachOptions}}
{{#unless hideHelp}}
    {{#if def.help}}
        <p class="help-block">{{str def.help module}}</p>
    {{/if}}
{{/unless}}
